import React, { useState } from "react";
import AbsoluteWrapper from "./AbsoluteWrapper";
import "../css/tiers.css";
import { Transition } from "react-transition-group";
import divine from "../img/BACKGROUNDDIVINE.png";

function Tiers() {
  const [switcher, setSwitch] = useState(false);
  const [currentTier, setTier] = useState("vindicator");

  return (
    <AbsoluteWrapper>
      <div className="tiersPage">
        <Tier switcher={true} />
        <button onClick={() => setSwitch(!switcher)}>here</button>
      </div>
    </AbsoluteWrapper>
  );
}

export default Tiers;

function Tier(props) {
  const transitionStyles = {
    entering: { opacity: 0.5 },
    entered: { opacity: 1 },
    exiting: { opacity: 0.5 },
    exited: { opacity: 0 },
  };

  const duration = 500;

  const defaultStyle = {
    transition: `opacity ${duration}ms ease-in-out`,
    opacity: 0,
  };

  return (
    <Transition in={props.switcher} timeout={duration}>
      {(state) => (
        <div
          style={{
            ...defaultStyle,
            ...transitionStyles[state],
          }}
        >
          <div className="tier">
            <h1>Tier 1</h1>
            <h2>The Vindicators</h2>
            {/* <img src={divine} alt="divine" /> */}
          </div>
        </div>
      )}
    </Transition>
  );
}
